chrome.runtime.onInstalled.addListener(()=>{console.log("GemScout extension installed")});chrome.action.onClicked.addListener(e=>{chrome.sidePanel.open({tabId:e.id})});chrome.runtime.onMessage.addListener((e,o,s)=>{var n;switch(console.log("Message received:",e),e.type){case"OPEN_SIDE_PANEL":(n=o.tab)!=null&&n.id&&chrome.sidePanel.open({tabId:o.tab.id});break;default:console.log("Unknown message type:",e.type)}s({success:!0})});
